import axios from "axios";
import { USER_LOGIN_FAIL } from "../constants/userConstants.js";
import { USER_LOGIN_SUCCESS } from "../constants/userConstants.js";
import { USER_LOGIN_REQUEST } from "../constants/userConstants.js";
import { USER_l } from "../constants/userConstants.js";

export const login = (email, password) => async (dispatch) => {
  try {
    dispatch({
      type: USER_LOGIN_REQUEST,
    });
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };
    const { data } = await axios.post(
      "/api/users/login",
      { email, password },
      config
    );
    dispatch({
      type: USER_LOGIN_SUCCESS,
      payload: data,
    });
    localStorage.setItem("userInfo", JSON.stringify(data));
  } catch (error) {
    dispatch({
      type: USER_LOGIN_FAIL,
      payload:
        error.response && error.response.data.message
          ? error.response.data.message
          : error.message,
    });
  }
};

export const logout = () => (dispatch) => {
  localStorage.removeItem ('userInfo')
  dispatch ({type: USER_LOGOUT})
}
